mapper.delete:
DELETE question
WHERE
	boardnum = #{boardnum}
	and
	userid = #{userID}

mapper.update:
UPDATE
	question
SET
	title = #{title},
	content = #{content}
WHERE
	boardnum = #{boardnum}
	and
	userid = #{userID}

mapper.delete:
DELETE
	reply_quest
WHERE
	replynum = #{replynum}
AND
	userid = #{userID}
